    public static int maximumProfit(int budgets[]) {
		// Write your code here
        Arrays.sort(budgets); // Sort the budgets in ascending order

        int n = budgets.length;
        int maxProfit = 0;

        for (int i = 0; i < n; i++) {
            int profit = budgets[i] * (n - i); // Calculate profit for each price
            maxProfit = Math.max(maxProfit, profit); // Update maximum profit
        }

        return maxProfit;
	}
